                  <UserIcon className="mb-3 h-6 w-6" />
                  <span className="text-sm font-medium">Patient</span>
                </Label>
                <Label
                  htmlFor="role-doctor-register"
                  className={`flex flex-col items-center justify-between rounded-md border-2 border-muted bg-popover p-4 hover:bg-accent hover:text-accent-foreground cursor-pointer ${
                    selectedRole === "doctor" ? "border-primary" : ""
                  }`}
                >
                  <RadioGroupItem value="doctor" id="role-doctor-register" className="sr-only" />
                  <Stethoscope className="mb-3 h-6 w-6" />
                  <span className="text-sm font-medium">Doctor</span>
                </Label>
              </RadioGroup>
            </div>

            {/* Doctor-specific fields */}
            {selectedRole === "doctor" && (
              <div className="space-y-2">
                <Label htmlFor="google-specialization">Specialization *</Label>
                <Select
                  value={selectedSpecialization}
                  onValueChange={setSelectedSpecialization}
                >
                  <SelectTrigger data-testid="select-google-specialization">
                    <SelectValue placeholder="Select your specialization" />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="Cardiologist">Cardiologist</SelectItem>
                    <SelectItem value="Dermatologist">Dermatologist</SelectItem>
                    <SelectItem value="Orthopedic">Orthopedic</SelectItem>
                    <SelectItem value="Neurologist">Neurologist</SelectItem>
                    <SelectItem value="Pediatrician">Pediatrician</SelectItem>
                    <SelectItem value="Ophthalmologist">Ophthalmologist</SelectItem>
                    <SelectItem value="General Physician">General Physician</SelectItem>
                    <SelectItem value="Psychiatrist">Psychiatrist</SelectItem>
                    <SelectItem value="Gynecologist">Gynecologist</SelectItem>
                    <SelectItem value="ENT Specialist">ENT Specialist</SelectItem>
                  </SelectContent>
                </Select>
              </div>
            )}

            {/* Patient-specific fields */}
            {selectedRole === "patient" && (
              <div className="grid grid-cols-2 gap-4">
                <div className="space-y-2">
                  <Label htmlFor="google-age">Age (optional)</Label>
                  <Input
                    id="google-age"
                    type="number"
                    placeholder="25"
                    value={selectedAge}
                    onChange={(e) => setSelectedAge(e.target.value)}
                    min="0"
                    max="120"
                    data-testid="input-google-age"
                  />
                </div>
                <div className="space-y-2">
                  <Label htmlFor="google-gender">Gender (optional)</Label>
                  <Select
                    value={selectedGender}
                    onValueChange={setSelectedGender}
                  >
                    <SelectTrigger data-testid="select-google-gender">
                      <SelectValue placeholder="Select" />
                    </SelectTrigger>
                    <SelectContent>
                      <SelectItem value="Male">Male</SelectItem>
                      <SelectItem value="Female">Female</SelectItem>
                      <SelectItem value="Other">Other</SelectItem>
                    </SelectContent>
                  </Select>
                </div>
              </div>
            )}

            <Button
              onClick={handleRoleSelection}
              className="w-full"
              disabled={isGoogleLoading}
              data-testid="button-confirm-role-register"
            >
              {isGoogleLoading ? (
                <>
                  <Loader2 className="mr-2 h-4 w-4 animate-spin" />
                  Signing up...
                </>
              ) : (
                "Continue"
              )}
            </Button>
          </div>
        </DialogContent>
      </Dialog>
    </div>
  );
}

server/firebase-admin.ts.content:
import admin from 'firebase-admin';
import { readFileSync, existsSync } from 'fs';

let firestore: admin.firestore.Firestore | null = null;
let firestoreAvailable = false;

async function testFirestoreConnection(): Promise<boolean> {
  if (!firestore) return false;

  try {
    // Try a simple operation to test if Firestore is actually accessible
    const testCollection = firestore.collection('_connection_test');
    await testCollection.limit(1).get();
    return true;
  } catch (error: any) {
    console.warn('Firestore connection test failed:', error.message);
    return false;
  }
}

try {
  const projectId = process.env.FIREBASE_PROJECT_ID || process.env.VITE_FIREBASE_PROJECT_ID;
  const serviceAccount = process.env.FIREBASE_SERVICE_ACCOUNT;
  const serviceAccountPath = process.env.FIREBASE_SERVICE_ACCOUNT_PATH;

  if (!projectId) {
    console.warn('Firebase project ID is not configured - using in-memory storage');
  } else {
    if (!admin.apps.length) {
      let credential;

      if (serviceAccount) {
        try {
          const serviceAccountData = JSON.parse(serviceAccount);
          credential = admin.credential.cert(serviceAccountData);
          console.log('âœ… Using Firebase service account credentials from environment variable');
        } catch (parseError) {
          console.error('Failed to parse service account JSON:', parseError);
          credential = admin.credential.applicationDefault();
        }
      } else if (serviceAccountPath && existsSync(serviceAccountPath)) {
        try {
          const serviceAccountData = JSON.parse(readFileSync(serviceAccountPath, 'utf8'));
          credential = admin.credential.cert(serviceAccountData);
          console.log(`âœ… Using Firebase service account credentials from file: ${serviceAccountPath}`);
        } catch (fileError) {
          console.error('Failed to read service account file:', fileError);
          credential = admin.credential.applicationDefault();
        }
      } else {
        console.warn('No valid Firebase service account found - using application default credentials');
        credential = admin.credential.applicationDefault();
      }

      admin.initializeApp({
        credential: credential,
        projectId: projectId,
      });
    }

    firestore = admin.firestore();
    console.log('Firebase Admin initialized - testing Firestore connection...');

    // Test connection asynchronously
    testFirestoreConnection().then(available => {
      firestoreAvailable = available;
      if (available) {
        console.log('âœ… Firestore is accessible and ready');
      } else {
        console.warn('âš ï¸  Firestore is not accessible - you need to either:');
        console.warn('   1. Enable Firestore in your Firebase Console, OR');
        console.warn('   2. Add a valid Firebase service account key');
        console.warn('   Using in-memory storage for now (data will not persist)');
        firestore = null;
      }
    });
  }
} catch (error) {
  console.error('Firebase Admin initialization error:', error);
}

export { firestore, firestoreAvailable };

