import type { Express } from "express";
import { createServer, type Server } from "http";
import { storage } from "./storage";
import { insertUserSchema, insertReportSchema, insertMedicationSchema, insertReminderSchema } from "@shared/schema";
import { analyzeMedicalReport, extractMedicationInfo, generateHealthSummary, translateMedicalText } from "./services/gemini";
import { extractTextFromImage, extractTextFromPDF, detectDocumentType } from "./services/ocr";
import { verifyFirebaseToken } from "./services/firebase-verify";
import { analyzeReportForSpecialization } from "./services/ai-doctor-matching";
import bcrypt from "bcrypt";
import session from "express-session";
import multer from "multer";
import { randomUUID } from "crypto";

// Configure multer for file uploads
const upload = multer({
  storage: multer.memoryStorage(),
  limits: {
    fileSize: 10 * 1024 * 1024, // 10MB limit
  },
  fileFilter: (req, file, cb) => {
    const allowedTypes = ['image/jpeg', 'image/png', 'image/jpg', 'application/pdf'];
    if (allowedTypes.includes(file.mimetype)) {
      cb(null, true);
    } else {
      cb(new Error('Invalid file type. Only JPEG, PNG, and PDF files are allowed.'));
    }
  },
});

export async function registerRoutes(app: Express): Promise<Server> {
  // Ensure SESSION_SECRET is set for security
  if (!process.env.SESSION_SECRET) {
    throw new Error('SESSION_SECRET environment variable is required for secure session management');
  }

  // Session middleware
  app.use(session({
    secret: process.env.SESSION_SECRET,
    resave: false,
    saveUninitialized: false,
    cookie: {
      secure: process.env.NODE_ENV === 'production',
      httpOnly: true,
      sameSite: process.env.NODE_ENV === 'production' ? 'none' : 'lax',
      maxAge: 24 * 60 * 60 * 1000, // 24 hours
    },
    name: 'sessionId', // Custom session name to avoid conflicts
  }));

  // Authentication middleware
  const requireAuth = (req: any, res: any, next: any) => {
    if (!req.session.userId) {
      return res.status(401).json({ message: 'Authentication required' });
    }
    next();
  };

  // Auth routes
  app.post('/api/auth/register', async (req, res) => {
    try {
      const userData = insertUserSchema.parse(req.body);

      // Check if user already exists
      const existingUser = await storage.getUserByEmail(userData.email);
      if (existingUser) {
        return res.status(400).json({ message: 'User already exists' });
      }

      if (!userData.password) {
        return res.status(400).json({ message: 'Password is required for email registration' });
      }

      // Validate specialization for doctors
      if (userData.role === 'doctor' && !userData.specialization) {
        return res.status(400).json({ message: 'Specialization is required for doctor registration' });
      }

      // Hash password
      const hashedPassword = await bcrypt.hash(userData.password, 10);

      const user = await storage.createUser({
        ...userData,
        password: hashedPassword,
      });

      req.session.userId = user.id;

      res.json({ 
        message: 'User created successfully', 
        user: { 
          id: user.id, 
          email: user.email, 
          firstName: user.firstName, 
          lastName: user.lastName,
          role: user.role 
        } 
      });
    } catch (error) {
      res.status(400).json({ message: error instanceof Error ? error.message : 'Operation failed' });
    }
  });

  app.post('/api/auth/login', async (req, res) => {
    try {
      const { email, password, role } = req.body;

      const user = await storage.getUserByEmail(email);
      if (!user) {
        return res.status(401).json({ message: 'Invalid credentials' });
      }

      if (user.role !== role) {
        return res.status(401).json({ message: 'Invalid credentials or role mismatch' });
      }

      if (!user.password) {
        return res.status(401).json({ message: 'Please use Google sign-in for this account' });
      }

      const isValidPassword = await bcrypt.compare(password, user.password);
      if (!isValidPassword) {
        return res.status(401).json({ message: 'Invalid credentials' });
      }

      req.session.userId = user.id;

      res.json({ 
        message: 'Login successful', 
        user: { 
          id: user.id, 
          email: user.email, 
          firstName: user.firstName, 
          lastName: user.lastName,
          role: user.role 
        } 
      });
    } catch (error) {
      res.status(400).json({ message: error instanceof Error ? error.message : 'Operation failed' });
    }
  });

  app.post('/api/auth/logout', (req, res) => {
    req.session.destroy((err) => {
      if (err) {
        return res.status(500).json({ message: 'Logout failed' });
      }
      res.json({ message: 'Logout successful' });
    });
  });

  // Profile routes
  app.get('/api/profile', requireAuth, async (req, res) => {
    try {
      const user = await storage.getUser(req.session.userId!);
      if (!user) {
        return res.status(404).json({ message: 'User not found' });
      }

      // Don't send password to client
      const { password, ...userWithoutPassword } = user;
      res.json(userWithoutPassword);
    } catch (error) {
      res.status(500).json({ message: error instanceof Error ? error.message : 'Operation failed' });
    }
  });

  app.patch('/api/profile', requireAuth, async (req, res) => {
    try {
      const userId = req.session.userId!;
      const updates = req.body;

      // Prevent updating sensitive fields
      delete updates.id;
      delete updates.password;
      delete updates.role;
      delete updates.firebaseUid;
      delete updates.authProvider;
      delete updates.createdAt;

      const updatedUser = await storage.updateUser(userId, updates);
      if (!updatedUser) {
        return res.status(404).json({ message: 'User not found' });
      }

      // Don't send password to client
      const { password, ...userWithoutPassword } = updatedUser;
      res.json(userWithoutPassword);
    } catch (error) {
      res.status(400).json({ message: error instanceof Error ? error.message : 'Operation failed' });
    }
  });

  app.delete('/api/profile', requireAuth, async (req, res) => {
    try {
      const userId = req.session.userId!;

      const success = await storage.deleteUser(userId);
      if (!success) {
        return res.status(500).json({ message: 'Failed to delete account' });
      }

      // Destroy session after deletion
      req.session.destroy(() => {
        res.json({ message: 'Account deleted successfully' });
      });
    } catch (error) {
      res.status(500).json({ message: error instanceof Error ? error.message : 'Operation failed' });
    }
  });

  app.post('/api/auth/firebase-login', async (req, res) => {
    try {
      const { idToken, role, specialization, age, gender } = req.body;

      if (!idToken) {
        return res.status(400).json({ message: 'ID token is required' });
      }

      // Verify Firebase ID token
      const verifiedToken = await verifyFirebaseToken(idToken);

      if (!verifiedToken.email) {
        return res.status(400).json({ message: 'Email not found in Firebase token' });
      }

      // Parse name from Firebase token
      const displayName = verifiedToken.name || '';
      const nameParts = displayName.split(' ');
      const firstName = nameParts[0] || 'User';
      const lastName = nameParts.slice(1).join(' ') || '';

      let user = await storage.getUserByEmail(verifiedToken.email);

      if (!user) {
        // Validate specialization for doctors
        if (role === 'doctor' && !specialization) {
          return res.status(400).json({ message: 'Specialization is required for doctor registration' });
        }

        const userData: any = {
          email: verifiedToken.email,
          firstName,
          lastName,
          role: role || 'patient',
          authProvider: 'google',
          firebaseUid: verifiedToken.uid,
        };

        // Add specialization for doctors
        if (role === 'doctor' && specialization) {
          userData.specialization = specialization;
        }

        // Add patient-specific fields
        if (role === 'patient') {
          if (age) userData.age = age;
          if (gender) userData.gender = gender;
        }

        user = await storage.createUser(userData);
      } else if (user.firebaseUid !== verifiedToken.uid) {
        await storage.updateUser(user.id, {
          firebaseUid: verifiedToken.uid,
          authProvider: 'google',
        });
      }

      req.session.userId = user.id;

      // Debug logging
      console.log('Firebase login successful:', {
        userId: user.id,
        sessionId: req.sessionID,
        hasSession: !!req.session
      });

      res.json({ 
        message: 'Login successful', 
        user: { 
          id: user.id, 
          email: user.email, 
          firstName: user.firstName, 
          lastName: user.lastName,
          role: user.role 
        } 
      });
    } catch (error) {
      console.error('Firebase login error:', error);
      res.status(401).json({ message: 'Authentication failed. Please try again.' });
    }
  });

  // Debug endpoint to check session state
  app.get('/api/auth/debug', (req, res) => {
    res.json({
      hasSession: !!req.session,
      userId: req.session?.userId,
      sessionId: req.sessionID,
      sessionData: req.session,
      cookies: req.headers.cookie
    });
  });

  app.get('/api/auth/me', requireAuth, async (req, res) => {
    try {
      const user = await storage.getUser(req.session.userId!);
      if (!user) {
        return res.status(404).json({ message: 'User not found' });
      }

      res.json({ 
        id: user.id, 
        email: user.email, 
        firstName: user.firstName, 
        lastName: user.lastName,
        role: user.role,
        language: user.language 
      });
    } catch (error) {
      res.status(500).json({ message: error instanceof Error ? error.message : 'Operation failed' });
    }
  });

  // Reports routes
  app.post('/api/reports/upload', requireAuth, upload.single('file'), async (req, res) => {
    try {
      if (!req.file) {
        return res.status(400).json({ message: 'No file uploaded' });
      }

      const fileId = randomUUID();
      const fileName = req.file.originalname;
      const fileUrl = `/uploads/${fileId}_${fileName}`;

      // Create report immediately without processing
      const report = await storage.createReport({
        userId: req.session.userId!,
        fileName,
        fileUrl,
        reportType: 'general', // Will be updated after processing
        originalText: '', // Will be updated after processing
        status: 'processing',
      });

      // Process in background (don't await this)
      processReportAsync(report.id, req.file.buffer, req.file.mimetype).catch(error => {
        console.error('Background processing failed for report', report.id, ':', error);
        // Update report status to failed
        storage.updateReport(report.id, {
          status: 'failed',
          summary: 'Processing failed due to technical error'
        }).catch(updateError => {
          console.error('Failed to update report status:', updateError);
        });
      });

      // Respond immediately to prevent timeout
      res.json({ message: 'File uploaded successfully', reportId: report.id });
    } catch (error) {
      console.error('Upload route error:', error);
      res.status(500).json({ message: error instanceof Error ? error.message : 'Upload failed' });
    }
  });

  // Background processing function
  async function processReportAsync(reportId: string, fileBuffer: Buffer, mimeType: string) {
    try {
      console.log(`Starting background processing for report ${reportId}`);

      // Extract text based on file type with proper error handling
      let extractedText = '';
      let reportType = 'general';
      let extractionFailed = false;

      try {
        if (mimeType === 'application/pdf') {
          console.log('Processing PDF...');
          extractedText = await extractTextFromPDF(fileBuffer);
        } else {
          console.log('Processing image with OCR...');
          const ocrResult = await extractTextFromImage(fileBuffer);
          extractedText = ocrResult.text;
        }

        if (extractedText && extractedText.length > 0) {
          reportType = detectDocumentType(extractedText);
          console.log(`Detected document type: ${reportType}`);
        }
      } catch (error) {
        console.error('Text extraction failed:', error);
        extractionFailed = true;
        extractedText = error instanceof Error ? error.message : 'Text extraction failed';
      }

      // Update report with extracted text
      await storage.updateReport(reportId, {
        originalText: extractedText,
        reportType: reportType,
      });

      let analysis = null;
      let extractedData = null;
      let summary = '';

      // Check if we have sufficient text content for analysis
      const hasMinimalContent = extractedText && extractedText.length > 50 && extractedText !== 'No text detected' && extractedText !== 'No text found in PDF';

      // Process analysis based on document type
      try {
        if (!hasMinimalContent) {
          console.log('Insufficient text content extracted. Document may be low quality or corrupted.');
          summary = 'âš ï¸ Document uploaded but OCR could not extract readable text. This may be due to:\n\n' +
                    'â€¢ Low image quality or resolution\n' +
                    'â€¢ Blurry or unclear text\n' +
                    'â€¢ Handwritten content (not supported)\n' +
                    'â€¢ Heavy shadows or glare on the document\n\n' +
                    'Please try:\n' +
                    '1. Re-scanning with higher quality (at least 300 DPI)\n' +
                    '2. Ensuring good lighting without shadows\n' +
                    '3. Taking a clear, straight photo of the document\n' +
                    '4. Using a PDF with selectable text instead of a scan';
          extractedData = { 
            message: 'Text extraction failed',
            extractedLength: extractedText?.length || 0,
            suggestion: 'Upload a higher quality scan or PDF with selectable text'
          };
        } else if (reportType === 'blood_test' || reportType === 'general') {
          console.log('Running medical analysis...');
          analysis = await analyzeMedicalReport(extractedText);
          summary = analysis.summary;
          extractedData = analysis;
        } else if (reportType === 'prescription') {
          console.log('Extracting medication info...');
          const medications = await extractMedicationInfo(extractedText);
          extractedData = { medications };
          summary = `Prescription contains ${medications.length} medication(s)`;

          // Create medication entries
          const report = await storage.getReport(reportId);
          if (report) {
            for (const med of medications) {
              try {
                await storage.createMedication({
                  userId: report.userId,
                  reportId: report.id,
                  name: med.name,
                  dosage: med.dosage,
                  frequency: med.frequency,
                  instructions: med.instructions,
                  sideEffects: med.sideEffects?.join(', ') || '',
                  isActive: true,
                });
              } catch (medError) {
                console.error('Failed to create medication:', medError);
              }
            }
          }
        }
      } catch (analysisError) {
        console.error('Analysis failed:', analysisError);
        summary = 'Document processed successfully. Professional medical review recommended.';
        extractedData = { message: 'Analysis unavailable - please consult healthcare provider' };
      }

      // Update report with final results
      const finalStatus = extractionFailed ? 'failed' : 'completed';
      await storage.updateReport(reportId, {
        analysis,
        extractedData,
        summary: extractionFailed ? extractedText : summary,
        status: finalStatus,
      });

      // Create timeline entry only if processing succeeded
      if (!extractionFailed) {
        try {
          const report = await storage.getReport(reportId);
          if (report) {
            // Properly structure timeline entry with comprehensive information
            const timelineEntry: any = {
              userId: report.userId,
              reportId: report.id,
              date: report.uploadedAt || report.createdAt || new Date(),
              eventType: reportType === 'prescription' ? 'prescription' : 
                        reportType === 'x-ray' || reportType === 'mri' || reportType === 'ct_scan' ? 'scan' : 
                        'uploaded_report',
              reportType: reportType,
              title: `${reportType.replace(/_/g, ' ')} - ${report.fileName}`,
              description: summary,
              summary: summary,
              fileUrl: report.fileUrl,
            };

            // Add analysis data if available
            if (analysis && typeof analysis === 'object') {
              timelineEntry.analysis = analysis;
              timelineEntry.riskLevel = analysis.riskLevel || null;

              // Extract specific metrics from analysis for easy display
              if (analysis.keyFindings && Array.isArray(analysis.keyFindings)) {
                const metrics: any = {};
                analysis.keyFindings.forEach((finding: any) => {
                  if (finding.parameter && finding.value) {
                    metrics[finding.parameter.toLowerCase().replace(/\s+/g, '_')] = finding.value;
                  }
                });
                if (Object.keys(metrics).length > 0) {
                  timelineEntry.metrics = metrics;
                }
              }
            }

            // Add medications for prescriptions
            if (reportType === 'prescription' && extractedData && typeof extractedData === 'object' && 'medications' in extractedData) {
              timelineEntry.medications = extractedData.medications;
            }

            // Determine severity level for scans
            if (reportType === 'x-ray' || reportType === 'mri' || reportType === 'ct_scan') {
              if (analysis && analysis.riskLevel) {
                timelineEntry.severityLevel = analysis.riskLevel === 'high' ? 'Critical' :
                                             analysis.riskLevel === 'medium' ? 'Moderate' : 'Low';
              }
            }

            await storage.createHealthTimelineEntry(timelineEntry);
          }
        } catch (timelineError) {
          console.error('Failed to create timeline entry:', timelineError);
          // Don't fail the whole process for timeline issues
        }
      }

      console.log(`Successfully completed processing for report ${reportId}`);
    } catch (error) {
      console.error('Report processing failed:', error);
      try {
        await storage.updateReport(reportId, {
          status: 'failed',
          summary: 'Processing failed due to technical error. Please try uploading again.',
        });
      } catch (updateError) {
        console.error('Failed to update report status:', updateError);
      }
    }
  }

