import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import {
  AlertDialog,
  AlertDialogAction,
  AlertDialogCancel,
  AlertDialogContent,
  AlertDialogDescription,
  AlertDialogFooter,
  AlertDialogHeader,
  AlertDialogTitle,
  AlertDialogTrigger,
} from "@/components/ui/alert-dialog";
import { Link } from "wouter";
import { useVoice } from "@/hooks/use-voice";
import { Play, FileText, Clock, Download, Share, Trash2 } from "lucide-react";
import { safeFormatDate } from "@/lib/date-utils";
import type { Report } from "@shared/schema";
import AnalysisSummary from "./analysis-summary";
import { useMutation } from "@tanstack/react-query";
import { apiRequest, queryClient } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";
import { useState } from "react";

interface ReportCardProps {
  report: Report;
}

export default function ReportCard({ report }: ReportCardProps) {
  const { speak } = useVoice();
  const { toast } = useToast();
  const [deleteDialogOpen, setDeleteDialogOpen] = useState(false);

  const deleteMutation = useMutation({
    mutationFn: async () => {
      const response = await fetch(`/api/reports/${report.id}`, {
        method: 'DELETE',
        credentials: 'include',
      });
      if (!response.ok) {
        const error = await response.json();
        throw new Error(error.message || 'Failed to delete report');
      }
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/reports'] });
      toast({
        title: "Report deleted",
        description: "The report has been successfully deleted from your records.",
      });
      setDeleteDialogOpen(false);
    },
    onError: (error: Error) => {
      toast({
        title: "Delete failed",
        description: error.message || "Failed to delete the report. Please try again.",
        variant: "destructive",
      });
    },
  });

  const handlePlayAudio = () => {
    if (report.summary) {
      speak(report.summary);
    } else {
      speak("Report analysis is still being processed.");
    }
  };

  const handleDownload = async () => {
    try {
      const response = await fetch(`/api/reports/${report.id}/download`, {
        method: 'GET',
        credentials: 'include',
      });

      if (!response.ok) {
        throw new Error('Download failed');
      }

      const blob = await response.blob();
      const url = window.URL.createObjectURL(blob);
      const a = document.createElement('a');
      a.href = url;
      a.download = `medical_report_${report.id}_${new Date().toISOString().split('T')[0]}.txt`;
      document.body.appendChild(a);
      a.click();
      window.URL.revokeObjectURL(url);
      document.body.removeChild(a);
    } catch (error) {
      console.error('Download failed:', error);
    }
  };

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'completed':
        return 'bg-green-100 text-green-800 border-green-200';
      case 'processing':
        return 'bg-amber-100 text-amber-800 border-amber-200';
      case 'failed':
        return 'bg-red-100 text-red-800 border-red-200';
      default:
        return 'bg-gray-100 text-gray-800 border-gray-200';
    }
  };

  const getReportTypeDisplay = (type: string) => {
    return type.replace('_', ' ').replace(/\b\w/g, l => l.toUpperCase());
  };

  return (
    <Card className="shadow-sm hover:shadow-md transition-shadow">
      <CardHeader className="border-b border-border">
        <div className="flex items-start justify-between">
          <div className="flex-1">
            <div className="flex items-center gap-2 mb-2">
              <CardTitle className="text-lg">
                {getReportTypeDisplay(report.reportType)}
              </CardTitle>
              <Badge className={getStatusColor(report.status || 'processing')}>
                {report.status || 'processing'}
              </Badge>
            </div>

            <p className="text-sm text-muted-foreground mb-1">
              {report.fileName}
            </p>

            <div className="flex items-center text-xs text-muted-foreground">
              <Clock className="h-3 w-3 mr-1" />
              {safeFormatDate(report.createdAt, 'MMM d, yyyy h:mm a')}
            </div>
          </div>

          <div className="flex items-center space-x-2">
            {report.summary && (
              <Button
                variant="ghost"
                size="sm"
                onClick={handlePlayAudio}
                title="Listen to report summary"
                data-testid={`play-audio-${report.id}`}
              >
                <Play className="h-4 w-4" />
              </Button>
            )}

            <Button
              variant="ghost"
              size="sm"
              onClick={handleDownload}
              disabled={report.status !== 'completed'}
              title={report.status === 'completed' ? 'Download report' : 'Report must be completed to download'}
              data-testid={`download-${report.id}`}
            >
              <Download className="h-4 w-4" />
            </Button>
          </div>
        </div>
      </CardHeader>

      <CardContent className="p-6">
        {report.status === 'completed' && report.analysis ? (
          <AnalysisSummary analysis={report.analysis} />
        ) : report.status === 'processing' ? (
          <div className="text-center py-6">
            <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-primary mx-auto mb-4"></div>
            <p className="text-sm text-muted-foreground">
              Processing your report...
            </p>
          </div>
        ) : report.status === 'failed' ? (
          <div className="text-center py-6">
            <FileText className="h-12 w-12 text-red-500 mx-auto mb-4" />
            <p className="text-sm text-red-600 mb-2">
              Failed to process report
            </p>
            <p className="text-xs text-muted-foreground">
              Please try uploading the document again
            </p>
          </div>
        ) : (
          <div className="bg-muted/50 rounded-lg p-4">
            <p className="text-sm text-muted-foreground">
              Report uploaded successfully. Analysis will appear here once processing is complete.
            </p>
          </div>
        )}

        {report.summary && (
          <div className="mt-4 p-4 bg-muted/50 rounded-lg">
            <h5 className="text-sm font-medium text-foreground mb-2">
              Summary:
            </h5>
            <p className="text-sm text-muted-foreground">
              {report.summary}
            </p>
          </div>
        )}

        <div className="mt-6 flex justify-between items-center">
          <div className="flex space-x-2">
            <Button 
              variant="outline" 
              size="sm"
              data-testid={`view-details-${report.id}`}
            >
              <FileText className="h-4 w-4 mr-2" />
              View Details
            </Button>

            {report.status === 'completed' && (
              <Button 
                variant="outline" 
                size="sm"
                data-testid={`share-report-${report.id}`}
              >
                <Share className="h-4 w-4 mr-2" />
                Share
              </Button>
            )}
          </div>

          <AlertDialog open={deleteDialogOpen} onOpenChange={setDeleteDialogOpen}>
            <AlertDialogTrigger asChild>
              <Button 
                variant="outline" 
                size="sm"
                className="text-destructive hover:text-destructive hover:bg-destructive/10"
                data-testid={`delete-report-${report.id}`}
              >
                <Trash2 className="h-4 w-4 mr-2" />
                Delete
              </Button>
            </AlertDialogTrigger>
            <AlertDialogContent>
              <AlertDialogHeader>
                <AlertDialogTitle>Delete Report?</AlertDialogTitle>
                <AlertDialogDescription>
                  Are you sure you want to delete this report? This action cannot be undone and will permanently remove the report from your records.
                </AlertDialogDescription>
              </AlertDialogHeader>
              <AlertDialogFooter>
                <AlertDialogCancel data-testid="cancel-delete">Cancel</AlertDialogCancel>
                <AlertDialogAction
                  onClick={() => deleteMutation.mutate()}
                  disabled={deleteMutation.isPending}
                  className="bg-destructive text-destructive-foreground hover:bg-destructive/90"
                  data-testid="confirm-delete"
                >
                  {deleteMutation.isPending ? "Deleting..." : "Delete"}
                </AlertDialogAction>
              </AlertDialogFooter>
            </AlertDialogContent>
          </AlertDialog>
        </div>
      </CardContent>
    </Card>
  );
}

client/src/pages/share.tsx.content:
import { useState } from "react";
import { useQuery } from "@tanstack/react-query";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Checkbox } from "@/components/ui/checkbox";
import SharingOptions from "@/components/share/sharing-options";
import { Share2, FileText, Users } from "lucide-react";

export default function Share() {
  const [selectedReports, setSelectedReports] = useState<string[]>([]);
  const [doctorEmail, setDoctorEmail] = useState("");

  const { data: reports, isLoading } = useQuery({
    queryKey: ["/api/reports"],
  });

  const completedReports = (reports || []).filter((report: any) => report.status === 'completed');

  const handleReportSelection = (reportId: string, checked: boolean) => {
    if (checked) {
      setSelectedReports([...selectedReports, reportId]);
    } else {
      setSelectedReports(selectedReports.filter(id => id !== reportId));
    }
  };

  const handleSelectAll = (checked: boolean) => {
    if (checked) {
      setSelectedReports(completedReports.map((report: any) => report.id));
    } else {
      setSelectedReports([]);
    }
  };

  return (
    <div className="space-y-6 fade-in">
      <div>
        <h1 className="text-3xl font-bold text-foreground mb-2">
          Share with Healthcare Provider
        </h1>
        <p className="text-muted-foreground">
          Generate shareable reports and summaries for your doctor visits
        </p>
      </div>

      {/* Quick Stats */}
      <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
        <Card>
          <CardContent className="p-6">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm text-muted-foreground">Available Reports</p>
                <p className="text-2xl font-bold text-foreground">
                  {completedReports.length}
                </p>
              </div>
              <FileText className="h-8 w-8 text-primary" />
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardContent className="p-6">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm text-muted-foreground">Selected Reports</p>
                <p className="text-2xl font-bold text-foreground">
                  {selectedReports.length}
                </p>
              </div>
              <Share2 className="h-8 w-8 text-green-600" />
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardContent className="p-6">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm text-muted-foreground">Previous Shares</p>
                <p className="text-2xl font-bold text-foreground">0</p>
              </div>
              <Users className="h-8 w-8 text-amber-600" />
            </div>
          </CardContent>
        </Card>
      </div>

      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
        {/* Report Selection */}
        <Card>
          <CardHeader>
            <CardTitle>Select Reports to Share</CardTitle>
            <div className="flex items-center space-x-2">
              <Checkbox
                id="select-all"
                checked={selectedReports.length === completedReports.length && completedReports.length > 0}
                onCheckedChange={handleSelectAll}
                data-testid="select-all-reports"
              />
              <Label htmlFor="select-all" className="text-sm">
                Select all reports
              </Label>
            </div>
          </CardHeader>

          <CardContent>
            {isLoading ? (
              <div className="space-y-4">
                {[...Array(3)].map((_, i) => (
                  <div key={i} className="flex items-center space-x-3 p-3 border border-border rounded-lg">
                    <div className="w-4 h-4 bg-muted rounded animate-pulse" />
                    <div className="flex-1 space-y-2">
                      <div className="h-4 bg-muted rounded animate-pulse" />
                      <div className="h-3 bg-muted rounded w-1/2 animate-pulse" />
                    </div>
                  </div>
                ))}
              </div>
            ) : completedReports.length === 0 ? (
              <div className="text-center py-8">
                <FileText className="h-12 w-12 text-muted-foreground mx-auto mb-4" />
                <p className="text-muted-foreground">No completed reports available to share</p>
              </div>
            ) : (
              <div className="space-y-3 max-h-96 overflow-y-auto">
                {completedReports.map((report: any) => (
                  <div
                    key={report.id}
                    className="flex items-center space-x-3 p-3 border border-border rounded-lg hover:bg-muted/50 transition-colors"
                    data-testid={`report-option-${report.id}`}
                  >
                    <Checkbox
                      id={`report-${report.id}`}
                      checked={selectedReports.includes(report.id)}
                      onCheckedChange={(checked) => handleReportSelection(report.id, checked as boolean)}
                    />
                    <div className="flex-1">
                      <Label 
                        htmlFor={`report-${report.id}`}
                        className="text-sm font-medium text-foreground cursor-pointer"
                      >
                        {report.fileName}
                      </Label>
                      <p className="text-xs text-muted-foreground">
                        {report.reportType.replace('_', ' ').toUpperCase()} â€¢ 
                        {new Date(report.createdAt).toLocaleDateString()}
                      </p>
                    </div>
                  </div>
                ))}
              </div>
            )}
          </CardContent>
        </Card>

        {/* Doctor Information */}
        <Card>
          <CardHeader>
            <CardTitle>Healthcare Provider Information</CardTitle>
          </CardHeader>

          <CardContent className="space-y-4">
            <div>
              <Label htmlFor="doctor-email">Doctor's Email (Optional)</Label>
              <Input
                id="doctor-email"
                type="email"
                placeholder="doctor@clinic.com"
                value={doctorEmail}
                onChange={(e) => setDoctorEmail(e.target.value)}
                data-testid="doctor-email-input"
              />
              <p className="text-xs text-muted-foreground mt-1">
                If provided, we'll send the report directly to your healthcare provider
              </p>
            </div>

            {/* Sharing Options */}
            <div className="pt-4 border-t border-border">
              <SharingOptions
                selectedReports={selectedReports}
                doctorEmail={doctorEmail}
                disabled={selectedReports.length === 0}
              />
            </div>
          </CardContent>
        </Card>
      </div>

      {/* Recent Shares */}
      <Card>
        <CardHeader>
          <CardTitle>Recent Shares</CardTitle>
        </CardHeader>

        <CardContent>
          <div className="text-center py-8">
            <Share2 className="h-12 w-12 text-muted-foreground mx-auto mb-4" />
            <p className="text-muted-foreground">No recent shares</p>
            <p className="text-sm text-muted-foreground mt-2">
              Your shared reports will appear here
            </p>
          </div>
        </CardContent>
      </Card>
    </div>
  );
}

