import type { DashboardStats } from "@/types/medical";

interface QuickStatsProps {
  stats?: DashboardStats;
}

export default function QuickStats({ stats }: QuickStatsProps) {
  const defaultStats = {
    totalReports: 0,
    activeMedications: 0,
    pendingReminders: 0,
    healthScore: "0%",
  };

  const currentStats = stats || defaultStats;

  const statItems = [
    {
      label: "Total Reports",
      value: currentStats.totalReports,
      icon: FileText,
      color: "text-primary",
      bgColor: "bg-primary/10",
    },
    {
      label: "Active Medications",
      value: currentStats.activeMedications,
      icon: Pill,
      color: "text-green-600",
      bgColor: "bg-green-100",
    },
    {
      label: "Pending Reminders",
      value: currentStats.pendingReminders,
      icon: Bell,
      color: "text-amber-600",
      bgColor: "bg-amber-100",
    },
    {
      label: "Health Score",
      value: currentStats.healthScore,
      icon: Heart,
      color: "text-green-600",
      bgColor: "bg-green-100",
    },
  ];

  return (
    <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
      {statItems.map((item, index) => {
        const Icon = item.icon;

        return (
          <Card key={index} className="shadow-sm">
            <CardContent className="p-6">
              <div className="flex items-center justify-between">
                <div>
                  <p className="text-sm text-muted-foreground">
                    {item.label}
                  </p>
                  <p 
                    className="text-2xl font-bold text-foreground"
                    data-testid={`stat-${item.label.toLowerCase().replace(/\s+/g, '-')}`}
                  >
                    {item.value}
                  </p>
                </div>
                <div className={`w-12 h-12 ${item.bgColor} rounded-lg flex items-center justify-center`}>
                  <Icon className={`h-6 w-6 ${item.color}`} />
                </div>
              </div>
            </CardContent>
          </Card>
        );
      })}
    </div>
  );
}

