/**
 * AI-Based Doctor Matching Service using Google Gemini
 * 
 * This service uses Google Gemini AI to analyze medical reports and detect:
 * - Abnormal medical parameters
 * - Health risks and conditions
 * - The most appropriate medical specialization needed
 * 
 * Available Specializations:
 * - Cardiologist, Dermatologist, Orthopedic, Neurologist, Gastroenterologist,
 * - Pulmonologist, Endocrinologist, Ophthalmologist, ENT Specialist, General Physician
 */

import { GoogleGenAI } from "@google/genai";

// Initialize Gemini AI (same as gemini.ts)
let gemini: GoogleGenAI | null = null;

if (process.env.GEMINI_API_KEY) {
  gemini = new GoogleGenAI({ apiKey: process.env.GEMINI_API_KEY });
} else {
  console.warn('Warning: GEMINI_API_KEY not set - AI doctor matching will use fallback');
}

/**
 * Uses Google Gemini AI to analyze a medical report and detect the appropriate
 * medical specialization based on abnormal parameters and health conditions.
 * 
 * @param reportText - Full text extracted from the medical report
 * @param symptoms - Patient-reported symptoms
 * @param summary - Report summary or description
 * @returns The detected medical specialization
 */
export async function analyzeReportWithAI(
  reportText?: string | null,
  symptoms?: string | null,
  summary?: string | null
): Promise<string> {
  // Combine all available text
  const combinedText = [
    reportText || '',
    symptoms || '',
    summary || ''
  ].filter(Boolean).join('\n\n');

  if (!combinedText.trim()) {
    console.log('âš ï¸ No text available for AI analysis - defaulting to General Physician');
    return 'General Physician';
  }

  if (!gemini) {
    console.log('âš ï¸ Gemini AI not available - defaulting to General Physician');
    return 'General Physician';
  }

  try {
    console.log('ðŸ¤– Analyzing medical report with Google Gemini AI...');

    const prompt = `You are a medical report analyzer. Analyze the following medical report and identify:
1. Any ABNORMAL medical parameters (values outside normal ranges)
2. Any health risks or conditions present
3. The PRIMARY medical specialization needed based on the abnormalities

Medical Report:
${combinedText}

Available Specializations:
- Cardiologist (heart, cardiovascular, blood pressure issues)
- Dermatologist (skin conditions)
- Orthopedic (bone, joint, muscle, spine issues)
- Neurologist (brain, nerve, neurological issues)
- Gastroenterologist (digestive, stomach, liver issues)
- Pulmonologist (lung, respiratory issues)
- Endocrinologist (diabetes, thyroid, hormonal issues)
- Ophthalmologist (eye, vision issues)
- ENT Specialist (ear, nose, throat issues)
- General Physician (general health or no specific abnormalities)

IMPORTANT INSTRUCTIONS:
1. Focus on ABNORMAL values and conditions mentioned in the report
2. If the report mentions bone, joint, spine, fracture, or orthopedic issues, suggest "Orthopedic"
3. If the report mentions heart, cardiac, blood pressure, or cardiovascular issues, suggest "Cardiologist"
4. Respond with ONLY the specialization name, nothing else
5. If no abnormalities are found or the report is unclear, respond with "General Physician"

Your response (single word only):`;

    const response = await gemini.models.generateContent({
      model: "gemini-2.5-flash",
      contents: prompt,
    });

    const aiResponse = (response.text || '').trim();

    // Extract the specialization from the response
    const validSpecializations = [
      'Cardiologist', 'Dermatologist', 'Orthopedic', 'Neurologist',
      'Gastroenterologist', 'Pulmonologist', 'Endocrinologist',
      'Ophthalmologist', 'ENT Specialist', 'General Physician'
    ];

    // Find matching specialization
    const detectedSpecialization = validSpecializations.find(spec => 
      aiResponse.toLowerCase().includes(spec.toLowerCase())
    ) || 'General Physician';

    console.log('ðŸŽ¯ AI Detection Results:', {
      analyzedTextLength: combinedText.length,
      aiResponse: aiResponse,
      detectedSpecialization,
      confidence: 'AI-powered'
    });

    return detectedSpecialization;

  } catch (error) {
    console.error('âŒ Error analyzing report with AI:', error);
    // Fallback to General Physician on error
    return 'General Physician';
  }
}

/**
 * Analyzes multiple text fields and returns the detected specialization
 * using AI-powered analysis of abnormal parameters.
 * 
 * @param symptoms - Patient-reported symptoms
 * @param summary - Report summary or AI-generated summary
 * @param description - Additional description or notes
 * @param reportText - Full extracted text from the medical report
 * @returns Object containing detected specialization and analysis details
 */
export async function analyzeReportForSpecialization(
  symptoms?: string | null,
  summary?: string | null,
  description?: string | null,
  reportText?: string | null
): Promise<{
  specialization: string;
  confidence: string;
  analyzedText: string;
}> {
  // Use AI to analyze the report
  const specialization = await analyzeReportWithAI(reportText, symptoms, summary);

  const combinedText = [
    reportText || '',
    symptoms || '',
    summary || '',
    description || ''
  ].filter(Boolean).join(' ');

  return {
    specialization,
    confidence: 'high', // AI-powered analysis
    analyzedText: combinedText.substring(0, 200)
  };
}

