import { useState } from "react";
import { useDropzone } from "react-dropzone";
import { useMutation } from "@tanstack/react-query";
import { Card, CardContent, CardHeader, CardTitle, CardDescription } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Textarea } from "@/components/ui/textarea";
import { Label } from "@/components/ui/label";
import { Progress } from "@/components/ui/progress";
import { useToast } from "@/hooks/use-toast";
import { useAuth } from "@/hooks/use-auth";
import { queryClient, apiRequest } from "@/lib/queryClient";
import { CloudUpload, File, CheckCircle, AlertCircle, Stethoscope, Loader2, UserCheck } from "lucide-react";

export default function UploadReportForm() {
  const [selectedFile, setSelectedFile] = useState<File | null>(null);
  const [symptoms, setSymptoms] = useState("");
  const [description, setDescription] = useState("");
  const [uploadProgress, setUploadProgress] = useState(0);
  const [suggestedDoctor, setSuggestedDoctor] = useState<any>(null);
  const [sharedReportId, setSharedReportId] = useState<string | null>(null);
  const [approvalStatus, setApprovalStatus] = useState<'pending' | 'approved'>('pending');
  const { toast } = useToast();
  const { user } = useAuth();

  const uploadMutation = useMutation({
    mutationFn: async (data: { file: File; symptoms: string; description: string }) => {
      // Step 1: Upload the file first
      const formData = new FormData();
      formData.append('file', data.file);

      const uploadResponse = await fetch('/api/reports/upload', {
        method: 'POST',
        body: formData,
        credentials: 'include',
      });

      if (!uploadResponse.ok) {
        throw new Error('File upload failed');
      }

      const uploadResult = await uploadResponse.json();
      const reportId = uploadResult.reportId;

      // Step 2: Wait a bit for the file to be processed and get the file URL
      await new Promise(resolve => setTimeout(resolve, 1000));

      // Step 3: Get the report details to get the fileUrl
      const reportResponse = await fetch(`/api/reports/${reportId}`, {
        credentials: 'include',
      });

      if (!reportResponse.ok) {
        throw new Error('Failed to fetch report details');
      }

      const reportDetails = await reportResponse.json();

      // Step 4: Call the uploadReport endpoint with symptoms and description
      const assignResponse = await fetch('/api/uploadReport', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          patientId: user?.id,
          reportId: reportId, // Pass the report ID instead of file URL
          reportURL: reportDetails.fileUrl, // Keep file URL for reference
          symptoms: data.symptoms,
          summary: data.symptoms, // Using symptoms as summary
          description: data.description,
        }),
        credentials: 'include',
      });

      if (!assignResponse.ok) {
        throw new Error('Doctor assignment failed');
      }

      return assignResponse.json();
    },
    onMutate: () => {
      setUploadProgress(0);
      setSuggestedDoctor(null);
      setSharedReportId(null);
      setApprovalStatus('pending');
      const interval = setInterval(() => {
        setUploadProgress(prev => Math.min(prev + 10, 90));
      }, 200);

      setTimeout(() => clearInterval(interval), 2000);
    },
    onSuccess: (data) => {
      setUploadProgress(100);
      setSuggestedDoctor(data.suggestedDoctor);
      setSharedReportId(data.reportId);
      setApprovalStatus(data.approvalStatus || 'pending');

      toast({
        title: "âœ… Report Analyzed Successfully!",
        description: `AI detected: ${data.aiDetection.detectedSpecialization}. Doctor suggested for your approval.`,
        duration: 6000,
      });

      // Invalidate queries to refresh data
      queryClient.invalidateQueries({ queryKey: ["/api/reports"] });
      queryClient.invalidateQueries({ queryKey: ["/api/timeline"] });
    },
    onError: (error) => {
      setUploadProgress(0);
      toast({
        title: "Upload Failed",
        description: error instanceof Error ? error.message : "Failed to upload report. Please try again.",
        variant: "destructive",
      });
    },
  });

  const approveMutation = useMutation({
    mutationFn: async (reportId: string) => {
      const response = await apiRequest('PUT', `/api/shared-reports/${reportId}/approve`);
      return response.json();
    },
    onSuccess: () => {
      setApprovalStatus('approved');
      toast({
        title: "âœ… Doctor Approved!",
        description: "The doctor will now be able to see your report and provide care.",
        duration: 5000,
      });

      // Invalidate queries to refresh both patient and doctor lists
      queryClient.invalidateQueries({ queryKey: ["/api/patient/doctors"] });
      queryClient.invalidateQueries({ queryKey: ["/api/doctor/patients"] });
      queryClient.invalidateQueries({ queryKey: ["/api/doctor/shared-reports"] });

      // Reset form after approval
      setTimeout(() => {
        setSelectedFile(null);
        setSymptoms("");
        setDescription("");
        setUploadProgress(0);
        setSuggestedDoctor(null);
        setSharedReportId(null);
        setApprovalStatus('pending');
      }, 3000);
    },
    onError: (error) => {
      toast({
        title: "Approval Failed",
        description: error instanceof Error ? error.message : "Failed to approve doctor. Please try again.",
        variant: "destructive",
      });
    },
  });

  const handleApprove = () => {
    if (sharedReportId) {
      approveMutation.mutate(sharedReportId);
    }
  };

  const onDrop = (acceptedFiles: File[]) => {
    if (acceptedFiles.length > 0) {
      const file = acceptedFiles[0];
      const validTypes = ['application/pdf', 'image/jpeg', 'image/png', 'image/jpg'];
      const maxSize = 10 * 1024 * 1024; // 10MB

      if (!validTypes.includes(file.type)) {
        toast({
          title: "Invalid File Type",
          description: "Please upload PDF, JPG, or PNG files only.",
          variant: "destructive",
        });
        return;
      }

      if (file.size > maxSize) {
        toast({
          title: "File Too Large",
          description: "Please upload files smaller than 10MB.",
          variant: "destructive",
        });
        return;
      }

      setSelectedFile(file);
    }
  };

  const { getRootProps, getInputProps, isDragActive } = useDropzone({
    onDrop,
    accept: {
      'application/pdf': ['.pdf'],
      'image/jpeg': ['.jpg', '.jpeg'],
      'image/png': ['.png'],
    },
    maxSize: 10 * 1024 * 1024,
    multiple: false,
  });

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();

    if (!selectedFile) {
      toast({
        title: "No File Selected",
        description: "Please select a file to upload.",
        variant: "destructive",
      });
      return;
    }

    if (!symptoms.trim()) {
      toast({
        title: "Symptoms Required",
        description: "Please enter your symptoms or reason for this report.",
        variant: "destructive",
      });
      return;
    }

    uploadMutation.mutate({
      file: selectedFile,
      symptoms,
      description,
    });
  };

  return (
    <Card className="shadow-sm">
      <CardHeader>
        <CardTitle>Upload Medical Report</CardTitle>
        <CardDescription>
          Upload your medical report and provide details for AI-powered doctor matching
        </CardDescription>
      </CardHeader>
      <CardContent>
        <form onSubmit={handleSubmit} className="space-y-6">
          {/* File Upload */}
          <div className="space-y-2">
            <Label htmlFor="file">Medical Report File *</Label>
            <div
              {...getRootProps()}
              className={`border-2 border-dashed rounded-xl p-8 text-center cursor-pointer transition-all duration-300 ${
                isDragActive
                  ? 'border-primary bg-primary/5 scale-105'
                  : 'border-border hover:border-primary hover:bg-primary/5'
              }`}
              data-testid="upload-zone"
            >
              <input {...getInputProps()} data-testid="file-input" id="file" />

              <div className="mx-auto w-16 h-16 bg-primary/10 rounded-full flex items-center justify-center mb-4">
                {selectedFile ? (
                  <File className="h-8 w-8 text-primary" />
                ) : (
                  <CloudUpload className="h-8 w-8 text-primary" />
                )}
              </div>

              {selectedFile ? (
                <div>
                  <h4 className="text-lg font-medium text-foreground mb-2">
                    {selectedFile.name}
                  </h4>
                  <p className="text-sm text-muted-foreground">
                    {(selectedFile.size / 1024 / 1024).toFixed(2)} MB
                  </p>
                </div>
              ) : (
                <div>
                  <h4 className="text-lg font-medium text-foreground mb-2">
                    Drop your file here
                  </h4>
                  <p className="text-muted-foreground mb-2">
                    or click to browse
                  </p>
                  <p className="text-xs text-muted-foreground">
                    Supports PDF, PNG, JPG files up to 10MB
                  </p>
                </div>
              )}
            </div>
          </div>

          {/* Symptoms Field */}
          <div className="space-y-2">
            <Label htmlFor="symptoms">Symptoms / Reason for Report *</Label>
            <Input
              id="symptoms"
              placeholder="e.g., Persistent headache, chest pain, fever..."
              value={symptoms}
              onChange={(e) => setSymptoms(e.target.value)}
              disabled={uploadMutation.isPending}
              data-testid="input-symptoms"
              required
            />
            <p className="text-xs text-muted-foreground">
              Describe your main symptoms or reason for this medical report
            </p>
          </div>

          {/* Description Field */}
          <div className="space-y-2">
            <Label htmlFor="description">Additional Details</Label>
            <Textarea
              id="description"
              placeholder="Provide any additional context, history, or concerns..."
              value={description}
              onChange={(e) => setDescription(e.target.value)}
              disabled={uploadMutation.isPending}
              rows={4}
              data-testid="input-description"
            />
            <p className="text-xs text-muted-foreground">
              Optional: Add more context about your condition or medical history
            </p>
          </div>

          {/* Upload Progress */}
          {uploadMutation.isPending && (
            <div className="space-y-2">
              <div className="flex items-center justify-between text-sm">
                <span className="text-muted-foreground">Uploading and processing...</span>
                <span className="font-medium text-foreground">{uploadProgress}%</span>
              </div>
              <Progress value={uploadProgress} className="h-2" />
            </div>
          )}

          {/* Success Message with Suggested Doctor */}
          {suggestedDoctor && (
            <Card className={approvalStatus === 'approved' ? 
              "bg-green-50 dark:bg-green-950 border-green-200 dark:border-green-800" :
              "bg-blue-50 dark:bg-blue-950 border-blue-200 dark:border-blue-800"
            }>
              <CardContent className="p-4">
                <div className="flex items-start space-x-3">
                  <div className={`w-10 h-10 rounded-full flex items-center justify-center flex-shrink-0 ${
                    approvalStatus === 'approved' ?
                    "bg-green-100 dark:bg-green-900" :
                    "bg-blue-100 dark:bg-blue-900"
                  }`}>
                    {approvalStatus === 'approved' ? (
                      <CheckCircle className="h-6 w-6 text-green-600 dark:text-green-400" />
                    ) : (
                      <Stethoscope className="h-6 w-6 text-blue-600 dark:text-blue-400" />
                    )}
                  </div>
                  <div className="flex-1">
                    <h4 className={`font-semibold mb-1 ${
                      approvalStatus === 'approved' ?
                      "text-green-900 dark:text-green-100" :
                      "text-blue-900 dark:text-blue-100"
                    }`}>
                      {approvalStatus === 'approved' ? 'Doctor Approved!' : 'Doctor Suggested for Approval'}
                    </h4>
                    <div className={`flex items-center space-x-2 text-sm ${
                      approvalStatus === 'approved' ?
                      "text-green-800 dark:text-green-200" :
                      "text-blue-800 dark:text-blue-200"
                    }`}>
                      <UserCheck className="h-4 w-4" />
                      <span>
                        <strong>Dr. {suggestedDoctor.firstName} {suggestedDoctor.lastName}</strong>
                      </span>
                    </div>
                    <p className={`text-sm mt-1 ${
                      approvalStatus === 'approved' ?
                      "text-green-700 dark:text-green-300" :
                      "text-blue-700 dark:text-blue-300"
                    }`}>
                      Specialization: {suggestedDoctor.specialization}
                    </p>

                    {approvalStatus === 'pending' && (
                      <div className="mt-3">
                        <p className="text-xs text-blue-600 dark:text-blue-400 mb-2">
                          Based on your symptoms, our AI recommends this specialist. Click below to approve.
                        </p>
                        <Button
                          onClick={handleApprove}
                          disabled={approveMutation.isPending}
                          className="w-full bg-blue-600 hover:bg-blue-700 text-white"
                          data-testid="button-approve-doctor"
                        >
                          {approveMutation.isPending ? (
                            <>
                              <Loader2 className="h-4 w-4 mr-2 animate-spin" />
                              Approving...
                            </>
                          ) : (
                            <>
                              <CheckCircle className="h-4 w-4 mr-2" />
                              Approve Doctor
                            </>
                          )}
                        </Button>
                      </div>
                    )}

                    {approvalStatus === 'approved' && (
                      <p className="text-sm text-green-600 dark:text-green-400 mt-2">
                        âœ“ The doctor can now see your report and provide care
                      </p>
                    )}
                  </div>
                </div>
              </CardContent>
            </Card>
          )}

          {/* Submit Button */}
          <Button
            type="submit"
            className="w-full"
            disabled={uploadMutation.isPending}
            data-testid="button-submit"
          >
            {uploadMutation.isPending ? (
              <>
                <Loader2 className="h-4 w-4 mr-2 animate-spin" />
                Uploading...
              </>
            ) : (
              <>
                <CloudUpload className="h-4 w-4 mr-2" />
                Upload Report
              </>
            )}
          </Button>
        </form>
      </CardContent>
    </Card>
  );
}

client/src/pages/profile.tsx.content:
import { useState, useEffect } from "react";
import { useQuery, useMutation } from "@tanstack/react-query";
import { useAuth } from "@/hooks/use-auth";
import { useLocation } from "wouter";
import { Card, CardContent, CardHeader, CardTitle, CardDescription } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { useToast } from "@/hooks/use-toast";
import { apiRequest, queryClient } from "@/lib/queryClient";
import { UserCircle, Save, Trash2, AlertTriangle, Loader2 } from "lucide-react";
import { 
  AlertDialog,
  AlertDialogAction,
  AlertDialogCancel,
  AlertDialogContent,
  AlertDialogDescription,
  AlertDialogFooter,
  AlertDialogHeader,
  AlertDialogTitle,
  AlertDialogTrigger,
} from "@/components/ui/alert-dialog";
import type { User } from "@shared/schema";

export default function ProfilePage() {
  const { user: authUser, logout } = useAuth();
  const [, navigate] = useLocation();
  const { toast } = useToast();
  const [isEditing, setIsEditing] = useState(false);

  const { data: profile, isLoading } = useQuery<User>({
    queryKey: ["/api/profile"],
  });

  const [formData, setFormData] = useState<Partial<User>>({});

  // Update formData when profile loads
  useEffect(() => {
    if (profile) {
      setFormData({
        firstName: profile.firstName,
        lastName: profile.lastName,
        phone: profile.phone || '',
        dateOfBirth: profile.dateOfBirth || '',
        specialization: profile.specialization || '',
        age: profile.age || undefined,
        gender: profile.gender || '',
      });
    }
  }, [profile]);

  const updateMutation = useMutation({
    mutationFn: async (updates: Partial<User>) => {
      const response = await apiRequest('PATCH', '/api/profile', updates);
      return response.json();
    },
    onSuccess: (data) => {
      toast({
        title: "Profile Updated",
        description: "Your profile has been updated successfully.",
      });
      queryClient.invalidateQueries({ queryKey: ["/api/profile"] });
      queryClient.invalidateQueries({ queryKey: ["/api/auth/me"] });
      setIsEditing(false);
    },
    onError: (error) => {
      toast({
        title: "Update Failed",
        description: error instanceof Error ? error.message : "Failed to update profile",
        variant: "destructive",
      });
    },
  });

  const deleteMutation = useMutation({
    mutationFn: async () => {
      const response = await apiRequest('DELETE', '/api/profile');
      return response.json();
    },
    onSuccess: () => {
      toast({
        title: "Account Deleted",
        description: "Your account has been permanently deleted.",
      });
      logout();
      navigate('/login');
    },
    onError: (error) => {
      toast({
        title: "Deletion Failed",
        description: error instanceof Error ? error.message : "Failed to delete account",
        variant: "destructive",
      });
    },
  });

  const handleSave = () => {
    updateMutation.mutate(formData);
  };

  const handleDelete = () => {
    deleteMutation.mutate();
  };

  const handleCancel = () => {
    if (profile) {
      setFormData({
        firstName: profile.firstName,
        lastName: profile.lastName,
        phone: profile.phone || '',
        dateOfBirth: profile.dateOfBirth || '',
        specialization: profile.specialization || '',
        age: profile.age || undefined,
        gender: profile.gender || '',
      });
    }
    setIsEditing(false);
  };

  if (isLoading) {
    return (
      <div className="flex items-center justify-center min-h-[400px]">
        <Loader2 className="h-8 w-8 animate-spin text-primary" />
      </div>
    );
  }

  if (!profile) {
    return (
      <div className="flex items-center justify-center min-h-[400px]">
        <p className="text-muted-foreground">Profile not found</p>
      </div>
    );
  }

  return (
    <div className="max-w-3xl mx-auto space-y-6 fade-in">
      <div className="flex items-center justify-between">
        <div className="flex items-center gap-3">
          <UserCircle className="h-8 w-8 text-primary" />
          <div>
            <h1 className="text-3xl font-bold text-foreground">Profile</h1>
            <p className="text-sm text-muted-foreground">
              Manage your personal information
            </p>
          </div>
        </div>
        {!isEditing && (
          <Button
            onClick={() => setIsEditing(true)}
            data-testid="button-edit-profile"
          >
            Edit Profile
          </Button>
        )}
      </div>

      <Card>
        <CardHeader>
          <CardTitle>Personal Information</CardTitle>
          <CardDescription>
            Your basic profile information
          </CardDescription>
        </CardHeader>
        <CardContent className="space-y-4">
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            <div className="space-y-2">
              <Label htmlFor="firstName">First Name</Label>
              <Input
                id="firstName"
                value={isEditing ? formData.firstName : profile.firstName}
                onChange={(e) => setFormData({ ...formData, firstName: e.target.value })}
                disabled={!isEditing}
                data-testid="input-firstName"
              />
            </div>

            <div className="space-y-2">
              <Label htmlFor="lastName">Last Name</Label>
              <Input
                id="lastName"
                value={isEditing ? formData.lastName : profile.lastName}
                onChange={(e) => setFormData({ ...formData, lastName: e.target.value })}
                disabled={!isEditing}
                data-testid="input-lastName"
              />
            </div>
          </div>

          <div className="space-y-2">
            <Label htmlFor="email">Email</Label>
            <Input
              id="email"
              type="email"
              value={profile.email}
              disabled
              className="bg-muted"
              data-testid="input-email"
            />
            <p className="text-xs text-muted-foreground">Email cannot be changed</p>
          </div>

          <div className="space-y-2">
            <Label htmlFor="phone">Phone Number</Label>
            <Input
              id="phone"
              type="tel"
              value={isEditing ? (formData.phone ?? '') : (profile.phone ?? '')}
              onChange={(e) => setFormData({ ...formData, phone: e.target.value })}
              disabled={!isEditing}
              placeholder="Enter phone number"
              data-testid="input-phone"
            />
          </div>

          <div className="space-y-2">
            <Label htmlFor="dateOfBirth">Date of Birth</Label>
            <Input
              id="dateOfBirth"
              type="date"
              value={isEditing ? (formData.dateOfBirth ?? '') : (profile.dateOfBirth ?? '')}
              onChange={(e) => setFormData({ ...formData, dateOfBirth: e.target.value })}
              disabled={!isEditing}
              data-testid="input-dateOfBirth"
            />
          </div>

          {profile.role === 'doctor' && (
            <div className="space-y-2">
              <Label htmlFor="specialization">Specialization</Label>
              <Input
                id="specialization"
                value={isEditing ? (formData.specialization ?? '') : (profile.specialization ?? '')}
                onChange={(e) => setFormData({ ...formData, specialization: e.target.value })}
                disabled={!isEditing}
                placeholder="e.g., Cardiology, Neurology"
                data-testid="input-specialization"
              />
            </div>
          )}

          {profile.role === 'patient' && (
            <>
              <div className="space-y-2">
                <Label htmlFor="age">Age</Label>
                <Input
                  id="age"
                  type="number"
                  value={isEditing ? (formData.age ?? '') : (profile.age ?? '')}
                  onChange={(e) => setFormData({ ...formData, age: parseInt(e.target.value) || undefined })}
                  disabled={!isEditing}
                  placeholder="Enter age"
                  data-testid="input-age"
                />
              </div>

              <div className="space-y-2">
                <Label htmlFor="gender">Gender</Label>
                <Input
                  id="gender"
                  value={isEditing ? (formData.gender ?? '') : (profile.gender ?? '')}
                  onChange={(e) => setFormData({ ...formData, gender: e.target.value })}
                  disabled={!isEditing}
                  placeholder="e.g., Male, Female, Other"
                  data-testid="input-gender"
                />
              </div>
            </>
          )}

          {isEditing && (
            <div className="flex gap-2 pt-4">
              <Button
                onClick={handleSave}
                disabled={updateMutation.isPending}
                className="flex-1"
                data-testid="button-save-profile"
              >
                {updateMutation.isPending ? (
                  <>
                    <Loader2 className="h-4 w-4 mr-2 animate-spin" />
                    Saving...
                  </>
                ) : (
                  <>
                    <Save className="h-4 w-4 mr-2" />
                    Save Changes
                  </>
                )}
              </Button>
              <Button
                onClick={handleCancel}
                variant="outline"
                disabled={updateMutation.isPending}
                data-testid="button-cancel-edit"
              >
                Cancel
              </Button>
            </div>
          )}
        </CardContent>
      </Card>

      <Card className="border-destructive">
        <CardHeader>
          <CardTitle className="text-destructive">Danger Zone</CardTitle>
          <CardDescription>
            Permanently delete your account and all associated data
          </CardDescription>
        </CardHeader>
        <CardContent>
          <AlertDialog>
            <AlertDialogTrigger asChild>
              <Button
                variant="destructive"
                data-testid="button-delete-account"
              >
                <Trash2 className="h-4 w-4 mr-2" />
                Delete Account
              </Button>
            </AlertDialogTrigger>
            <AlertDialogContent>
              <AlertDialogHeader>
                <AlertDialogTitle className="flex items-center gap-2">
                  <AlertTriangle className="h-5 w-5 text-destructive" />
                  Are you absolutely sure?
                </AlertDialogTitle>
                <AlertDialogDescription>
                  This action cannot be undone. This will permanently delete your account
                  and remove all your data from our servers, including:
                  <ul className="list-disc list-inside mt-2 space-y-1">
                    <li>All medical reports</li>
                    <li>Medication records</li>
                    <li>Health timeline</li>
                    <li>Reminders</li>
                    <li>Shared reports and doctor assignments</li>
                  </ul>
                </AlertDialogDescription>
              </AlertDialogHeader>
              <AlertDialogFooter>
                <AlertDialogCancel data-testid="button-cancel-delete">
                  Cancel
                </AlertDialogCancel>
                <AlertDialogAction
                  onClick={handleDelete}
                  disabled={deleteMutation.isPending}
                  className="bg-destructive text-destructive-foreground hover:bg-destructive/90"
                  data-testid="button-confirm-delete"
                >
                  {deleteMutation.isPending ? (
                    <>
                      <Loader2 className="h-4 w-4 mr-2 animate-spin" />
                      Deleting...
                    </>
                  ) : (
                    'Delete Account'
                  )}
                </AlertDialogAction>
              </AlertDialogFooter>
            </AlertDialogContent>
          </AlertDialog>
        </CardContent>
      </Card>
    </div>
  );
}
